mapper.select:
select competition.id id , competition.title title , user.nickname createUserNickName , date_format(competition.startTime, '%Y-%m-%d %h:%i') startTime, date_format(competition.endTime, '%Y-%m-%d %h:%i') endTime,
competition.isPublic isPublic , competition.description description,
if(competition_user.username is not null ,true ,false ) isUserJoined,
if(now() between competition.startTime and competition.endTime,'processing',if(now() > competition.endTime,'end','unstart')) status
from competition left join user on competition.createUsername=user.username
left join competition_user on competition.id = competition_user.competitionId
where 1
and competition.id=#{competitionId}
and createUsername=#{createUserName}
and competition.isDel=false
